import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ImportantNotePR = () => {
  const [note, setNote] = useState('');
  const [noteId, setNoteId] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get('/api/important-note-pr')
      .then((res) => {
        if (res.data) {
          setNote(res.data.message);
          setNoteId(res.data._id);
        }
      });
  }, []);

  const handleSave = () => {
    axios.post('/api/important-note-pr', { message: note, author: 'User' })
      .then((res) => {
        setMessage('Note saved!');
        setNoteId(res.data._id);
      });
  };

  const handleDelete = () => {
    axios.delete(`/api/important-note-pr/${noteId}`)
      .then(() => {
        setNote('');
        setNoteId(null);
        setMessage('Note deleted!');
      });
  };

  return (
    <div>
      <h3 style={{ marginBottom: '10px' }}>üìù Important Notes</h3>
      <textarea
        rows={6}
        style={{
          width: '150%',
          padding: '10px',
          fontSize: '1rem',
          border: '1px solid #ccc',
          borderRadius: '5px',
          resize: 'vertical',
          overflowY: 'auto',
          maxHeight: '150px'
        }}
        placeholder="Type here..."
        value={note}
        onChange={(e) => setNote(e.target.value)}
      ></textarea>
      <div style={{ marginTop: '10px' }}>
        <button onClick={handleSave}>üíæ Save</button>
        {noteId && (
          <button onClick={handleDelete} style={{ marginLeft: '10px' }}>üóë Delete</button>
        )}
      </div>
      {message && <p style={{ color: 'green' }}>{message}</p>}
    </div>
  );
};

export default ImportantNotePR;
