import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useUserRole } from '../context/UserRoleContext';
import logo from '../assets/clinic-logo.png';

const pageLabels = {
  patientRecords: 'Patient Records',
  appointments: 'Appointments',
  aiAssistant: 'AI Dental Assistant',
  finance: 'Finance & Billing',
  inventory: 'Inventory Management',
  lab: 'Lab Work Tracking',
  illustrations: 'Illustrations',
  notes: 'Dental Diary',
};

const quickLinksData = {
  patientRecords: '/patient-records',
  appointments: '/appointments',
  aiAssistant: '/ai-assistant',
  finance: '/finance',
  inventory: '/inventory',
  lab: '/lab-tracking',
  illustrations: '/illustrations',
  notes: '/notes',
};

const Home = () => {
  const { role } = useUserRole();
  const navigate = useNavigate();

  const pageAccess = JSON.parse(localStorage.getItem('pageAccess')) || {};
  const isAdmin = role === 'admin';

  const allowedPages = isAdmin
    ? Object.keys(pageLabels)
    : Object.keys(pageAccess[role] || {}).filter((page) => pageAccess[role][page]);

  return (
    <div className="home-container">
      <div className="clinic-header">
        <div className="logo-title">
          <img src={logo} alt="Clinic Logo" className="home-logo" />
          <h1>Elite Dental Clinic</h1>
        </div>
        <p className="doctor-names">
          Dr. Chikku Paulo, B.D.S &nbsp;&nbsp;&nbsp; Dr. Gayathri Balagopal, B.D.S
        </p>
      </div>

      {allowedPages.length === 0 ? (
        <p style={{ textAlign: 'center', color: 'gray' }}>
          No pages unlocked for this role. Please contact admin.
        </p>
      ) : (
        <div className="quick-links">
          {allowedPages.map((key) => (
            <div key={key} className="card" onClick={() => navigate(quickLinksData[key])}>
              <h3>{pageLabels[key]}</h3>
              <button>Go</button>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default Home;
