import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUserRole } from '../context/UserRoleContext';

const LoginPage = () => {
  const [role, setRole] = useState('');
  const navigate = useNavigate();
  const { setRole: setUserRole } = useUserRole();

  const handleLogin = () => {
    if (role === 'admin') {
      const password = prompt('Enter Admin Password');
      if (password === 'admin123') {
        setUserRole('admin');
        navigate('/home');
      } else {
        alert('Incorrect password');
      }
    } else if (role) {
      setUserRole(role);
      navigate('/home');
    } else {
      alert('Please choose a role');
    }
  };

  return (
    <div className="login-container">
      <h2 className="login-title">SELECT YOUR ROLE</h2>
      <div className="login-box">
        <select className="login-select" value={role} onChange={(e) => setRole(e.target.value)}>
          <option value="">-- Choose Role --</option>
          <option value="assistant">Assistant</option>
          <option value="lab">Lab Worker</option>
          <option value="admin">Administrator</option>
        </select>
        <button className="login-button" onClick={handleLogin}>Continue</button>
      </div>
    </div>
  );
};

export default LoginPage;
