import React, { useState } from 'react';
import './PatientForm.css';

const PatientForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    phone: '',
    address: '',
    date: '',
    referredBy: '',
    maritalStatus: '',
    chiefComplaint: [],
    medicalHistory: [],
    allergies: '',
    medications: '',
    dentalHistory: [],
    ccOther: '',
    dhOther: '',
    showChiefComplaint: false,
    showMedicalHistory: false,
    showDentalHistory: false,
    clinicalFindings: {
      first: {},
      second: {},
      third: {},
      fourth: {}
    }
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleCheckboxChange = (key, value) => {
    setFormData((prev) => {
      const updated = prev[key].includes(value)
        ? prev[key].filter(item => item !== value)
        : [...prev[key], value];
      return { ...prev, [key]: updated };
    });
  };

  const handleToothMultiSelectChange = (quadrant, toothNumber, selectedOptions) => {
    const selectedValues = Array.from(selectedOptions, (option) => option.value);
    setFormData(prev => {
      const updated = { ...prev.clinicalFindings };
      updated[quadrant][toothNumber] = selectedValues;
      return { ...prev, clinicalFindings: updated };
    });
  };

  const renderQuadrantTable = (quadrant) => {
    const teeth = ['11', '12', '13', '14', '15', '16', '17', '18'];
    const options = ['C', 'M', 'X', 'F'];
    return (
      <div className="tooth-table">
        <h4>{quadrant.charAt(0).toUpperCase() + quadrant.slice(1)} Quadrant</h4>
        <table>
          <thead>
            <tr>
              <th>Tooth No.</th>
              <th>Condition</th>
            </tr>
          </thead>
          <tbody>
            {teeth.map(tooth => (
              <tr key={`${quadrant}-${tooth}`}>
                <td>{tooth}</td>
                <td>
                  <select
                    multiple
                    value={formData.clinicalFindings[quadrant][tooth] || []}
                    onChange={(e) => handleToothMultiSelectChange(quadrant, tooth, e.target.selectedOptions)}
                  >
                    {options.map(opt => (
                      <option key={opt} value={opt}>{opt}</option>
                    ))}
                  </select>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    alert('Patient data saved successfully!');
  };

  return (
    <div className="form-container">
      <h2>Dental OP Case Sheet</h2>
      <form onSubmit={handleSubmit}>
        <fieldset>
          <legend>Patient Information</legend>
          <label>Name: <input type="text" name="name" value={formData.name} onChange={handleChange} /></label>
          <label>Age: <input type="number" name="age" value={formData.age} onChange={handleChange} /></label>
          <label>Gender:
            <select name="gender" value={formData.gender} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
            </select>
          </label>
          <label>Phone: <input type="text" name="phone" value={formData.phone} onChange={handleChange} /></label>
          <label>Address: <input type="text" name="address" value={formData.address} onChange={handleChange} /></label>
          <label>Date: <input type="date" name="date" value={formData.date} onChange={handleChange} /></label>
          <label>Referred By:
            <select name="referredBy" value={formData.referredBy} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Self">Self</option>
              <option value="Doctor">Doctor</option>
              <option value="Family/Friend">Family/Friend</option>
              <option value="Online">Online</option>
            </select>
          </label>
          <label>Marital Status:
            <select name="maritalStatus" value={formData.maritalStatus} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Single">Single</option>
              <option value="Married">Married</option>
            </select>
          </label>
        </fieldset>

        {/* Chief Complaint */}
        <fieldset>
          <legend>Chief Complaint</legend>
          <div className="dropdown">
            <button type="button" className="dropdown-toggle" onClick={() => setFormData(prev => ({ ...prev, showChiefComplaint: !prev.showChiefComplaint }))}>Select Complaints</button>
            {formData.showChiefComplaint && (
              <div className="dropdown-menu">
                {['Tooth Pain', 'Swelling', 'Bleeding Gums', 'Sensitivity', 'Bad Breath', 'Tooth Missing', 'Cosmetic Issue'].map(cc => (
                  <label key={cc}>
                    <input type="checkbox" checked={formData.chiefComplaint.includes(cc)} onChange={() => handleCheckboxChange('chiefComplaint', cc)} /> {cc}
                  </label>
                ))}
              </div>
            )}
          </div>
          <label>Others: <input type="text" name="ccOther" value={formData.ccOther} onChange={handleChange} /></label>
        </fieldset>

        {/* Medical History */}
        <fieldset>
          <legend>Medical History</legend>
          <div className="dropdown">
            <button type="button" className="dropdown-toggle" onClick={() => setFormData(prev => ({ ...prev, showMedicalHistory: !prev.showMedicalHistory }))}>Select Conditions</button>
            {formData.showMedicalHistory && (
              <div className="dropdown-menu">
                {['None', 'Diabetes', 'Hypertension', 'Kidney Disease', 'Heart Disease', 'Cancer', 'Asthma', 'Bleeding Disorders'].map(mh => (
                  <label key={mh}>
                    <input type="checkbox" checked={formData.medicalHistory.includes(mh)} onChange={() => handleCheckboxChange('medicalHistory', mh)} /> {mh}
                  </label>
                ))}
              </div>
            )}
          </div>
          <label>Allergies: <input type="text" name="allergies" value={formData.allergies} onChange={handleChange} /></label>
          <label>Medications: <input type="text" name="medications" value={formData.medications} onChange={handleChange} /></label>
        </fieldset>

        {/* Dental History */}
        <fieldset>
          <legend>Dental History</legend>
          <div className="dropdown">
            <button type="button" className="dropdown-toggle" onClick={() => setFormData(prev => ({ ...prev, showDentalHistory: !prev.showDentalHistory }))}>Select Dental History</button>
            {formData.showDentalHistory && (
              <div className="dropdown-menu">
                {['First Visit', 'RCT', 'Extraction', 'Filling', 'Scaling', 'Crown/Denture'].map(dh => (
                  <label key={dh}>
                    <input type="checkbox" checked={formData.dentalHistory.includes(dh)} onChange={() => handleCheckboxChange('dentalHistory', dh)} /> {dh}
                  </label>
                ))}
              </div>
            )}
          </div>
          <label>Others: <input type="text" name="dhOther" value={formData.dhOther} onChange={handleChange} /></label>
        </fieldset>

        {/* Clinical Findings */}
        <fieldset>
          <legend>Clinical Findings</legend>
          {['first', 'second', 'third', 'fourth'].map(q => renderQuadrantTable(q))}
        </fieldset>

        <button type="submit">Save Patient</button>
      </form>
    </div>
  );
};

export default PatientForm;
