// PatientForm.js
import React, { useState, useEffect, useRef } from 'react';
import './PatientForm.css';

const PatientForm = () => {
  const [formData, setFormData] = useState({
    name: '', age: '', gender: '', phone: '', address: '', date: '', referredBy: '', maritalStatus: '',
    chiefComplaint: '', medicalHistory: '', allergies: '', medications: '', dentalHistory: '',
    ccOther: '', dhOther: '', showTable: false, selectedQuadrant: '', investigations: '',
    otherInvestigation: '', uploadedFiles: [], diagnosis: '', treatmentPlan: '', otherTreatmentPlan: '',
    clinicalFindings: {
      first: Array(8).fill({ cavity: false, mobility: false, missing: false, filling: false }),
      second: Array(8).fill({ cavity: false, mobility: false, missing: false, filling: false }),
      third: Array(8).fill({ cavity: false, mobility: false, missing: false, filling: false }),
      fourth: Array(8).fill({ cavity: false, mobility: false, missing: false, filling: false }),
    },
    orthoProfile: {
      facialProfile: '', lipCompetency: '', overjet: '', overbite: '', habits: [],
      molarRelation: '', crossbite: '', openbite: ''
    },
    prescriptions: [{ drugName: '', dosage: '', duration: '' }],
    department: [],
    dentistName: '',
  });

  const tableRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (tableRef.current && !tableRef.current.contains(event.target) && !event.target.closest('select[name="selectedQuadrant"]')) {
        setFormData((prev) => ({ ...prev, showTable: false }));
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleQuadrantSelect = (e) => {
    setFormData((prev) => ({
      ...prev,
      selectedQuadrant: e.target.value,
      showTable: true,
    }));
  };

  const handleToothCheckboxChange = (quadrant, index, field) => {
    const updatedTeeth = [...formData.clinicalFindings[quadrant]];
    updatedTeeth[index] = { ...updatedTeeth[index], [field]: !updatedTeeth[index][field] };
    setFormData((prev) => ({
      ...prev,
      clinicalFindings: {
        ...prev.clinicalFindings,
        [quadrant]: updatedTeeth,
      },
    }));
  };

  const handleFileChange = (e) => {
    setFormData((prev) => ({ ...prev, uploadedFiles: [...e.target.files] }));
  };

  const handleOrthoChange = (field, value) => {
    setFormData((prev) => ({
      ...prev,
      orthoProfile: {
        ...prev.orthoProfile,
        [field]: value,
      },
    }));
  };

  const handleOrthoHabitChange = (habit) => {
    setFormData((prev) => {
      const habits = prev.orthoProfile.habits.includes(habit)
        ? prev.orthoProfile.habits.filter((h) => h !== habit)
        : [...prev.orthoProfile.habits, habit];
      return {
        ...prev,
        orthoProfile: {
          ...prev.orthoProfile,
          habits,
        },
      };
    });
  };

  const handlePrescriptionChange = (index, field, value) => {
    const updated = [...formData.prescriptions];
    updated[index][field] = value;
    setFormData((prev) => ({ ...prev, prescriptions: updated }));
  };

  const addPrescriptionRow = () => {
    setFormData((prev) => ({
      ...prev,
      prescriptions: [...prev.prescriptions, { drugName: '', dosage: '', duration: '' }],
    }));
  };

  const handleCheckboxChange = (field, value) => {
    setFormData((prev) => {
      const updated = prev[field].includes(value)
        ? prev[field].filter((item) => item !== value)
        : [...prev[field], value];
      return { ...prev, [field]: updated };
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    alert('Patient data saved successfully!');
  };

  const renderQuadrantTable = (quadrant) => (
    <div ref={tableRef} className="quadrant-table">
      <table className="tooth-table">
        <thead>
          <tr>
            <th>Tooth No.</th>
            <th>Cavity</th>
            <th>Mobility</th>
            <th>Missing</th>
            <th>Filling</th>
          </tr>
        </thead>
        <tbody>
          {formData.clinicalFindings[quadrant].map((tooth, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              {['cavity', 'mobility', 'missing', 'filling'].map((field) => (
                <td key={field}>
                  <input
                    type="checkbox"
                    checked={tooth[field]}
                    onChange={() => handleToothCheckboxChange(quadrant, index, field)}
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  return (
    <div className="form-container">
      <h2>Dental OP Case Sheet</h2>
      <form onSubmit={handleSubmit}>

       {/* Patient Info */}
        <fieldset>
          <legend>Patient Information</legend>
          <label>Name: <input type="text" name="name" value={formData.name} onChange={handleChange} /></label>
          <label>Age: <input type="number" name="age" value={formData.age} onChange={handleChange} /></label>
          <label>Gender:
            <select name="gender" value={formData.gender} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
            </select>
          </label>
          <label>Phone: <input type="text" name="phone" value={formData.phone} onChange={handleChange} /></label>
          <label>Address: <input type="text" name="address" value={formData.address} onChange={handleChange} /></label>
          <label>Date: <input type="date" name="date" value={formData.date} onChange={handleChange} /></label>
          <label>Referred By:
            <select name="referredBy" value={formData.referredBy} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Self">Self</option>
              <option value="Doctor">Doctor</option>
              <option value="Family/Friend">Family/Friend</option>
              <option value="Online">Online</option>
            </select>
          </label>
          <label>Marital Status:
            <select name="maritalStatus" value={formData.maritalStatus} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Single">Single</option>
              <option value="Married">Married</option>
            </select>
          </label>
        </fieldset>

        {/* Chief Complaint */}
        <fieldset>
          <legend>Chief Complaint</legend>
          <select name="chiefComplaint" value={formData.chiefComplaint} onChange={handleChange}>
            <option value="">--Select--</option>
            <option value="Tooth Pain">Tooth Pain</option>
            <option value="Swelling">Swelling</option>
            <option value="Bleeding Gums">Bleeding Gums</option>
            <option value="Sensitivity">Sensitivity</option>
            <option value="Bad Breath">Bad Breath</option>
            <option value="Tooth Missing">Tooth Missing</option>
            <option value="Cosmetic Issue">Cosmetic Issue</option>
          </select>
          <label>Others: <input type="text" name="ccOther" value={formData.ccOther} onChange={handleChange} /></label>
        </fieldset>

        {/* Medical History */}
        <fieldset>
          <legend>Medical History</legend>
          <select name="medicalHistory" value={formData.medicalHistory} onChange={handleChange}>
            <option value="">--Select--</option>
            <option value="None">None</option>
            <option value="Diabetes">Diabetes</option>
            <option value="Hypertension">Hypertension</option>
            <option value="Kidney Disease">Kidney Disease</option>
            <option value="Heart Disease">Heart Disease</option>
            <option value="Cancer">Cancer</option>
            <option value="Asthma">Asthma</option>
            <option value="Bleeding Disorders">Bleeding Disorders</option>
          </select>
          <label>Allergies: <input type="text" name="allergies" value={formData.allergies} onChange={handleChange} /></label>
          <label>Medications: <input type="text" name="medications" value={formData.medications} onChange={handleChange} /></label>
        </fieldset>

        {/* Dental History */}
        <fieldset>
          <legend>Dental History</legend>
          <select name="dentalHistory" value={formData.dentalHistory} onChange={handleChange}>
            <option value="">--Select--</option>
            <option value="First Visit">First Visit</option>
            <option value="RCT">RCT</option>
            <option value="Extraction">Extraction</option>
            <option value="Filling">Filling</option>
            <option value="Scaling">Scaling</option>
            <option value="Crown/Denture">Crown/Denture</option>
          </select>
          <label>Others: <input type="text" name="dhOther" value={formData.dhOther} onChange={handleChange} /></label>
        </fieldset>

        {/* Clinical Findings */}
        <fieldset>
          <legend>Clinical Findings</legend>
          <label>Quadrant:
            <select name="selectedQuadrant" value={formData.selectedQuadrant} onChange={handleQuadrantSelect}>
              <option value="">--Select Quadrant--</option>
              <option value="first">First Quadrant</option>
              <option value="second">Second Quadrant</option>
              <option value="third">Third Quadrant</option>
              <option value="fourth">Fourth Quadrant</option>
            </select>
          </label>
          {formData.showTable && renderQuadrantTable(formData.selectedQuadrant)}
        </fieldset>

        {/* Investigations */}
        <fieldset>
          <legend>Investigations</legend>
          <select name="investigations" value={formData.investigations} onChange={handleChange}>
            <option value="">--Select--</option>
            <option value="IOPA X-Ray">IOPA X-Ray</option>
            <option value="OPG">OPG</option>
            <option value="CBCT">CBCT</option>
            <option value="Pulp Vitality Test">Pulp Vitality Test</option>
            <option value="Others">Others</option>
          </select>
          {formData.investigations === 'Others' && (
            <label>
              Specify Others:
              <textarea
                name="otherInvestigation"
                rows="3"
                style={{ width: '100%', marginTop: '10px' }}
                value={formData.otherInvestigation}
                onChange={handleChange}
              />
            </label>
          )}
          <label> Upload Documents :
            <input type="file" multiple onChange={handleFileChange} accept="image/*, .pdf, .doc, .docx" />
          </label>
        </fieldset>

        {/* Diagnosis */}
        <fieldset>
          <legend>Diagnosis</legend>
          <textarea
            name="diagnosis"
            rows="4"
            maxLength="300"
            value={formData.diagnosis}
            onChange={handleChange}
            placeholder="Write diagnosis here..."
            style={{ width: '100%' }}
          />
        </fieldset>

        {/* Treatment Plan */}
        <fieldset>
          <legend>Treatment Plan</legend>
          <select name="treatmentPlan" value={formData.treatmentPlan} onChange={handleChange}>
            <option value="">--Select--</option>
            <option value="Filling">Filling</option>
            <option value="RCT">RCT</option>
            <option value="Extraction">Extraction</option>
            <option value="Crown">Crown</option>
            <option value="Scaling">Scaling</option>
            <option value="Braces">Braces</option>
            <option value="Implant">Implant</option>
            <option value="Others">Others</option>
          </select>
          {formData.treatmentPlan === 'Others' && (
            <label>
              Specify Others:
              <textarea
                name="otherTreatmentPlan"
                rows="3"
                style={{ width: '100%', marginTop: '10px' }}
                value={formData.otherTreatmentPlan}
                onChange={handleChange}
              />
            </label>
          )}
        </fieldset>
        <fieldset>
  <legend>Ortho Profile</legend>

  <label>Facial Profile:</label>
  <div className="checkbox-group">
    {['Straight', 'Convex', 'Concave'].map((type) => (
      <label key={type}>
        <input
          type="radio"
          name="facialProfile"
          value={type}
          checked={formData.orthoProfile.facialProfile === type}
          onChange={(e) => handleOrthoChange('facialProfile', e.target.value)}
        />
        {type}
      </label>
    ))}
  </div>

  <label>Lip Competency:</label>
  <div className="checkbox-group">
    {['Competent', 'Incompetent'].map((status) => (
      <label key={status}>
        <input
          type="radio"
          name="lipCompetency"
          value={status}
          checked={formData.orthoProfile.lipCompetency === status}
          onChange={(e) => handleOrthoChange('lipCompetency', e.target.value)}
        />
        {status}
      </label>
    ))}
  </div>

  <label>Overjet / Overbite:
    <input
      type="text"
      name="overjet"
      value={formData.orthoProfile.overjet}
      onChange={(e) => handleOrthoChange('overjet', e.target.value)}
      placeholder="Overjet mm"
    /> / 
    <input
      type="text"
      name="overbite"
      value={formData.orthoProfile.overbite}
      onChange={(e) => handleOrthoChange('overbite', e.target.value)}
      placeholder="Overbite mm"
    />
  </label>

  <label>Habits:</label>
  <div className="checkbox-group">
    {['Thumb Sucking', 'Mouth Breathing', 'None'].map((habit) => (
      <label key={habit}>
        <input
          type="checkbox"
          checked={formData.orthoProfile.habits.includes(habit)}
          onChange={() => handleOrthoHabitChange(habit)}
        />
        {habit}
      </label>
    ))}
  </div>

  <label>Molar Relation:</label>
  <div className="checkbox-group">
    {['Class I', 'Class II', 'Class III'].map((relation) => (
      <label key={relation}>
        <input
          type="radio"
          name="molarRelation"
          value={relation}
          checked={formData.orthoProfile.molarRelation === relation}
          onChange={(e) => handleOrthoChange('molarRelation', e.target.value)}
        />
        {relation}
      </label>
    ))}
  </div>

  <label>Crossbite / Openbite:</label>
  <div>
    <label>
      Crossbite:
      <select
        name="crossbite"
        value={formData.orthoProfile.crossbite}
        onChange={(e) => handleOrthoChange('crossbite', e.target.value)}
      >
        <option value="">--Select--</option>
        <option value="Yes">Yes</option>
        <option value="No">No</option>
      </select>
    </label>
    <label>
      Openbite:
      <select
        name="openbite"
        value={formData.orthoProfile.openbite}
        onChange={(e) => handleOrthoChange('openbite', e.target.value)}
      >
        <option value="">--Select--</option>
        <option value="Yes">Yes</option>
        <option value="No">No</option>
      </select>
    </label>
  </div>
</fieldset>

<fieldset>
  <legend>Prescription</legend>
  <table>
    <thead>
      <tr>
        <th>Drug Name</th>
        <th>Dosage</th>
        <th>Duration</th>
      </tr>
    </thead>
    <tbody>
      {formData.prescriptions.map((prescription, index) => (
        <tr key={index}>
          <td><input type="text" value={prescription.drugName} onChange={(e) => handlePrescriptionChange(index, 'drugName', e.target.value)} /></td>
          <td><input type="text" value={prescription.dosage} onChange={(e) => handlePrescriptionChange(index, 'dosage', e.target.value)} /></td>
          <td><input type="text" value={prescription.duration} onChange={(e) => handlePrescriptionChange(index, 'duration', e.target.value)} /></td>
        </tr>
      ))}
    </tbody>
  </table>
  <button type="button" onClick={addPrescriptionRow}>+ Add Row</button>
</fieldset>

<fieldset>
  <legend>Department</legend>
  {['General Dentistry','Pedodontics','Orthodontics','Conservative Dentistry','Periodontics','Oral Surgery','Prosthodontics','Oral Medicine & Radiology','Endodontics','Community Dentistry'].map((dept) => (
    <label key={dept}>
      <input
        type="checkbox"
        checked={formData.department.includes(dept)}
        onChange={() => handleCheckboxChange('department', dept)}
      />
      {dept}
    </label>
  ))}
</fieldset>

<fieldset>
  <legend>Dentist's Name</legend>
  <input type="text" name="dentistName" value={formData.dentistName} onChange={handleChange} placeholder="Dr. _____________________" />
</fieldset>

        <button type="submit">Save Patient</button>
      </form>
    </div>
  );
};

export default PatientForm;
