// PatientForm.js
import axios from 'axios';
import React, { useState, useEffect, useRef } from 'react';
import './PatientForm.css';
import toothChartImg from '../../assets/tooth-chart.png'; // Add your image here

const problemOptions = ['Caries', 'Mobility', 'Periodontitis', 'Prosthesis'];

const PatientForm = () => {
  const [formData, setFormData] = useState({
    name: '', age: '', gender: '', phone: '', address: '', date: '', referredBy: '',
    maritalStatus: '', chiefComplaint: '', medicalHistory: '', allergies: '', medications: '',
    dentalHistory: '', ccOther: '', dhOther: '', investigations: '', otherInvestigation: '',
    uploadedFiles: [], diagnosis: '', treatmentPlan: '', otherTreatmentPlan: '',
    department: '', dentistName: '',
    clinicalFindings: {
      first: Array(8).fill({ cavity: false, mobility: false, missing: false, filling: false })
    },
    selectedQuadrant: '', showTable: false,
    orthoProfile: {
      facialProfile: '', lipCompetency: '', overjet: '', overbite: '',
      habits: '', molarRelation: '', crossbite: '', openbite: ''
    },
    prescriptions: [],
    toothConditions: {}  // ðŸ¦· NEW
  });

  const [selectedTooth, setSelectedTooth] = useState(null);
  const [showToothBox, setShowToothBox] = useState(false);
  const [toothDetails, setToothDetails] = useState({ problems: [], note: '' });

  const handleToothClick = (toothNo) => {
    const details = formData.toothConditions[toothNo] || { problems: [], note: '' };
    setSelectedTooth(toothNo);
    setToothDetails(details);
    setShowToothBox(true);
  };

  const handleProblemChange = (problem) => {
    setToothDetails(prev => ({
      ...prev,
      problems: prev.problems.includes(problem)
        ? prev.problems.filter(p => p !== problem)
        : [...prev.problems, problem]
    }));
  };

  const handleToothNoteChange = (e) => {
    setToothDetails(prev => ({ ...prev, note: e.target.value }));
  };

  const saveToothDetails = () => {
    setFormData(prev => ({
      ...prev,
      toothConditions: {
        ...prev.toothConditions,
        [selectedTooth]: toothDetails
      }
    }));
    setShowToothBox(false);
  };

  const closeToothBox = () => setShowToothBox(false);

  const renderToothChart = () => {
    const toothNumbers = [
      18,17,16,15,14,13,12,11,21,22,23,24,25,26,27,28,
      48,47,46,45,44,43,42,41,31,32,33,34,35,36,37,38
    ];

    return (
      <div className="tooth-chart">
        <img src={toothChartImg} alt="Tooth Chart" className="chart-img" />
        {toothNumbers.map((no, idx) => (
          <button
            key={no}
            className={`tooth-button ${formData.toothConditions[no] ? 'active-tooth' : ''}`}
            style={{ top: `${Math.floor(idx / 16) * 160 + 30}px`, left: `${(idx % 16) * 35 + 10}px` }}
            onClick={() => handleToothClick(no)}
          >
            {no}
          </button>
        ))}
        {showToothBox && (
          <div className="tooth-popup">
            <h4>Tooth {selectedTooth}</h4>
            <div>
              {problemOptions.map(option => (
                <label key={option}>
                  <input
                    type="checkbox"
                    checked={toothDetails.problems.includes(option)}
                    onChange={() => handleProblemChange(option)}
                  /> {option}
                </label>
              ))}
            </div>
            <textarea
              rows="3"
              placeholder="Describe issue..."
              value={toothDetails.note}
              onChange={handleToothNoteChange}
            />
            <div className="popup-buttons">
              <button onClick={saveToothDetails}>Save</button>
              <button onClick={closeToothBox}>Close</button>
            </div>
          </div>
        )}
      </div>
    );
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/patients', formData);
      alert('Patient data saved successfully!');
    } catch (error) {
      console.error('Error saving patient:', error);
      alert('Failed to save patient.');
    }
  };

  return (
    <div className="form-container">
      <h1>Dental OP Case Sheet</h1>
      <form onSubmit={handleSubmit}>
        {/* Existing Sections Stay UNCHANGED */}
        {/* ...Insert existing fieldsets like Patient Info, History, Ortho, etc... */}

        {/* ðŸ¦· Tooth Chart Section */}
        <fieldset>
          <legend>Tooth Conditions</legend>
          {renderToothChart()}
        </fieldset>

        <button type="submit">Save Patient</button>
      </form>
    </div>
  );
};

export default PatientForm;
