import React, { useState } from 'react';
import './PatientForm.css';

const PatientForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    phone: '',
    address: '',
    date: '',
    referredBy: '',
    maritalStatus: '',
    chiefComplaint: [],
    medicalHistory: [],
    allergies: '',
    medications: '',
    dentalHistory: [],
    ccOther: '',
    dhOther: '',
    showChiefComplaint: false,
    showMedicalHistory: false,
    showDentalHistory: false,
    clinicalFindings: {
      first: Array(8).fill(null).map(() => ({ cavity: false, mobility: false, missing: false, filling: false })),
      second: Array(8).fill(null).map(() => ({ cavity: false, mobility: false, missing: false, filling: false })),
      third: Array(8).fill(null).map(() => ({ cavity: false, mobility: false, missing: false, filling: false })),
      fourth: Array(8).fill(null).map(() => ({ cavity: false, mobility: false, missing: false, filling: false })),
    },
    selectedQuadrant: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleMultiSelectChange = (e, key) => {
    const options = Array.from(e.target.selectedOptions, (option) => option.value);
    setFormData((prev) => ({ ...prev, [key]: options }));
  };

  const handleCheckboxChange = (key, value) => {
    setFormData((prev) => {
      const updated = prev[key].includes(value)
        ? prev[key].filter(item => item !== value)
        : [...prev[key], value];
      return { ...prev, [key]: updated };
    });
  };

  const handleToothCheckboxChange = (quadrant, index, field) => {
    const updatedQuadrant = [...formData.clinicalFindings[quadrant]];
    updatedQuadrant[index] = {
      ...updatedQuadrant[index],
      [field]: !updatedQuadrant[index][field],
    };
    setFormData((prev) => ({
      ...prev,
      clinicalFindings: {
        ...prev.clinicalFindings,
        [quadrant]: updatedQuadrant,
      },
    }));
  };

  const renderQuadrantTable = (quadrant) => {
    return (
      <table className="tooth-table">
        <thead>
          <tr>
            <th>Tooth No.</th>
            <th>Cavity</th>
            <th>Mobility</th>
            <th>Missing</th>
            <th>Filling</th>
          </tr>
        </thead>
        <tbody>
          {formData.clinicalFindings[quadrant].map((tooth, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              {['cavity', 'mobility', 'missing', 'filling'].map((field) => (
                <td key={field}>
                  <input
                    type="checkbox"
                    checked={tooth[field]}
                    onChange={() => handleToothCheckboxChange(quadrant, index, field)}
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    alert('Patient data saved successfully!');
  };

  return (
    <div className="form-container">
      <h2>Dental OP Case Sheet</h2>
      <form onSubmit={handleSubmit}>
        <fieldset>
          <legend>Patient Information</legend>
          <label>Name: <input type="text" name="name" value={formData.name} onChange={handleChange} /></label>
          <label>Age: <input type="number" name="age" value={formData.age} onChange={handleChange} /></label>
          <label>Gender:
            <select name="gender" value={formData.gender} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
            </select>
          </label>
          <label>Phone: <input type="text" name="phone" value={formData.phone} onChange={handleChange} /></label>
          <label>Address: <input type="text" name="address" value={formData.address} onChange={handleChange} /></label>
          <label>Date: <input type="date" name="date" value={formData.date} onChange={handleChange} /></label>
          <label>Referred By:
            <select name="referredBy" value={formData.referredBy} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Self">Self</option>
              <option value="Doctor">Doctor</option>
              <option value="Family/Friend">Family/Friend</option>
              <option value="Online">Online</option>
            </select>
          </label>
          <label>Marital Status:
            <select name="maritalStatus" value={formData.maritalStatus} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Single">Single</option>
              <option value="Married">Married</option>
            </select>
          </label>
        </fieldset>

        <fieldset>
          <legend>Chief Complaint</legend>
          <label>Select complaints:
            <select multiple value={formData.chiefComplaint} onChange={(e) => handleMultiSelectChange(e, 'chiefComplaint')}>
              {['Tooth Pain', 'Swelling', 'Bleeding Gums', 'Sensitivity', 'Bad Breath', 'Tooth Missing', 'Cosmetic Issue'].map(cc => (
                <option key={cc} value={cc}>{cc}</option>
              ))}
            </select>
          </label>
          <label>Others: <input type="text" name="ccOther" value={formData.ccOther} onChange={handleChange} /></label>
        </fieldset>

        <fieldset>
          <legend>Medical History</legend>
          <label>Select medical conditions:
            <select multiple value={formData.medicalHistory} onChange={(e) => handleMultiSelectChange(e, 'medicalHistory')}>
              {['None', 'Diabetes', 'Hypertension', 'Kidney Disease', 'Heart Disease', 'Cancer', 'Asthma', 'Bleeding Disorders'].map(mh => (
                <option key={mh} value={mh}>{mh}</option>
              ))}
            </select>
          </label>
          <label>Allergies: <input type="text" name="allergies" value={formData.allergies} onChange={handleChange} /></label>
          <label>Medications: <input type="text" name="medications" value={formData.medications} onChange={handleChange} /></label>
        </fieldset>

        <fieldset>
          <legend>Dental History</legend>
          <label>Select previous dental care:
           <select name="selectedQuadrant" value={formData.selectedQuadrant} onChange={handleChange}>
              <option value="">--Select Quadrant--</option>
              {['First Visit', 'RCT', 'Extraction', 'Filling', 'Scaling', 'Crown/Denture'].map(dh => (
                <option key={dh} value={dh}>{dh}</option>
              ))}
            </select>
          </label>
          <label>Others: <input type="text" name="dhOther" value={formData.dhOther} onChange={handleChange} /></label>
        </fieldset>

        <fieldset>
          <legend>Clinical Findings (Tooth-wise)</legend>
          <label>Quadrant:
            <select name="selectedQuadrant" value={formData.selectedQuadrant} onChange={handleChange}>
              <option value="">--Select Quadrant--</option>
              <option value="first">First Quadrant</option>
              <option value="second">Second Quadrant</option>
              <option value="third">Third Quadrant</option>
              <option value="fourth">Fourth Quadrant</option>
            </select>
          </label>
          {formData.selectedQuadrant && renderQuadrantTable(formData.selectedQuadrant)}
        </fieldset>

        <button type="submit">Save Patient</button>
      </form>
    </div>
  );
};

export default PatientForm;
