import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { IconButton } from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';

const ImportantNotePR = () => {
  const [note, setNote] = useState('');
  const [noteId, setNoteId] = useState(null);
  const [message, setMessage] = useState('');
  const [isEditable, setIsEditable] = useState(true);

  useEffect(() => {
    axios.get('/api/important-note-pr')
      .then((res) => {
        if (res.data) {
          setNote(res.data.message);
          setNoteId(res.data._id);
        }
      });
  }, []);

  const handleSave = () => {
    axios.post('/api/important-note-pr', { message: note, author: 'User' })
      .then((res) => {
        setMessage('Note saved!');
        setNoteId(res.data._id);
      });
  };

  const handleDelete = () => {
    axios.delete(`/api/important-note-pr/${noteId}`)
      .then(() => {
        setNote('');
        setNoteId(null);
        setMessage('Note deleted!');
      });
  };

  const handleEdit = () => setIsEditable(true);

  return (
    <div style={{
      padding: '15px',
      borderRadius: '15px',
      backgroundColor: 'white',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
      maxWidth: '400px',
      height: '100%',
      display: 'flex',
      flexDirection: 'column',
      justifyContent: 'space-between'
    }}>
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '10px'
      }}>
        <h3 style={{ margin: 0, display: 'flex', alignItems: 'center' }}>
          üìù Important Notes
        </h3>
        <div>
          <IconButton onClick={handleSave} title="Save">
            <SaveIcon />
          </IconButton>
          {noteId && (
            <>
              <IconButton onClick={handleEdit} title="Edit">
                <EditIcon />
              </IconButton>
              <IconButton onClick={handleDelete} title="Delete">
                <DeleteIcon />
              </IconButton>
            </>
          )}
        </div>
      </div>

      <textarea
        rows={6}
        style={{
          width: '100%',
          padding: '10px',
          fontSize: '1rem',
          border: '1px solid #ccc',
          borderRadius: '8px',
          resize: 'none',
          overflowY: 'auto',
          height: '130px'
        }}
        placeholder="Type here..."
        value={note}
        onChange={(e) => setNote(e.target.value)}
        readOnly={!isEditable}
      ></textarea>

      {message && (
        <p style={{ color: 'green', marginTop: '10px' }}>{message}</p>
      )}
    </div>
  );
};

export default ImportantNotePR;
