import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Card, CardContent, Typography, TextField, Button, Stack } from '@mui/material';

const ImportantNotePR = () => {
  const [note, setNote] = useState('');
  const [noteId, setNoteId] = useState(null);
  const [status, setStatus] = useState('');

  useEffect(() => {
    axios.get('/api/important-note-pr')
      .then(res => {
        if (res.data) {
          setNote(res.data.message);
          setNoteId(res.data._id);
        }
      })
      .catch(err => console.log(err));
  }, []);

  const handleSave = () => {
    axios.post('/api/important-note-pr', { message: note, author: 'User' })
      .then(res => {
        setNoteId(res.data._id);
        setStatus('Note saved!');
        setTimeout(() => setStatus(''), 2000);
      });
  };

  const handleDelete = () => {
    axios.delete(`/api/important-note-pr/${noteId}`)
      .then(() => {
        setNote('');
        setNoteId(null);
        setStatus('Note deleted!');
        setTimeout(() => setStatus(''), 2000);
      });
  };

  return (
    <Card sx={{ mt: 4, background: '#fff8e1' }}>
      <CardContent>
        <Typography variant="h6" gutterBottom>
          üìù Important Note for Patient Records
        </Typography>

        <TextField
          fullWidth
          multiline
          rows={4}
          value={note}
          placeholder="Write your message here..."
          onChange={(e) => setNote(e.target.value)}  
        />

        <Stack direction="row" spacing={2} sx={{ mt: 2 }}>
          <Button variant="contained" color="primary" onClick={handleSave}>
            üíæ Save
          </Button>
          {noteId && (
            <Button variant="outlined" color="error" onClick={handleDelete}>
              üóë Delete
            </Button>
          )}
        </Stack>

        {status && (
          <Typography sx={{ mt: 1 }} color="green">
            {status}
          </Typography>
        )}
      </CardContent>
    </Card>
  );
};

export default ImportantNotePR;
