import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { IconButton } from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AssignmentIcon from '@mui/icons-material/Assignment';

const ImportantNotePR = () => {
  const [note, setNote] = useState('');
  const [noteId, setNoteId] = useState(null);
  const [message, setMessage] = useState('');
  const [isEditable, setIsEditable] = useState(false);

  useEffect(() => {
    axios.get('/api/important-note-pr')
      .then((res) => {
        if (res.data) {
          setNote(res.data.message);
          setNoteId(res.data._id);
        }
      });
  }, []);

  const handleSave = () => {
    axios.post('/api/important-note-pr', { message: note, author: 'User' })
      .then((res) => {
        setMessage('Note saved!');
        setNoteId(res.data._id);
        setIsEditable(false);
      });
  };

  const handleDelete = () => {
    axios.delete(`/api/important-note-pr/${noteId}`)
      .then(() => {
        setNote('');
        setNoteId(null);
        setMessage('Note deleted!');
        setIsEditable(false);
      });
  };

  const handleEdit = () => {
    setIsEditable(true);
  };

  return (
    <div
      style={{
        padding: '15px',
        borderRadius: '15px',
        backgroundColor: 'white',
        boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
        maxWidth: '100%',
        width: '100%',
        minWidth: '250px',
        height: '100%',
        boxSizing: 'border-box'
      }}
    >
      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '10px',
          gap: '10px'
        }}
      >
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <AssignmentIcon style={{ fontSize: '28px' }} />
          <h3 style={{ margin: 0, fontSize: '1.2rem' }}>Important Notes</h3>
        </div>
        <div style={{ display: 'flex', gap: '6px' }}>
          {!isEditable && (
            <IconButton onClick={handleEdit} title="Edit" style={{ backgroundColor: '#007b8a', color: 'white' }}>
              <EditIcon />
            </IconButton>
          )}
          {isEditable && (
            <>
              <IconButton onClick={handleSave} title="Save" style={{ backgroundColor: '#007b8a', color: 'white' }}>
                <SaveIcon />
              </IconButton>
              <IconButton onClick={handleDelete} title="Delete" style={{ backgroundColor: '#dc3545', color: 'white' }}>
                <DeleteIcon />
              </IconButton>
            </>
          )}
        </div>
      </div>

      <textarea
        rows={6}
        style={{
          width: '100%',
          padding: '12px',
          fontSize: '1rem',
          border: '2px solid #333',
          borderRadius: '10px',
          backgroundColor: '#f7f7f7',
          color: '#333',
          resize: 'none',
          overflowY: 'auto',
          height: '130px',
          fontFamily: 'inherit',
          boxSizing: 'border-box'
        }}
        placeholder="Type here......."
        value={note}
        onChange={(e) => setNote(e.target.value)}
        readOnly={!isEditable}
      ></textarea>

      {message && (
        <p style={{ color: 'green', marginTop: '10px', fontSize: '0.9rem' }}>{message}</p>
      )}
    </div>
  );
};

export default ImportantNotePR;
