import React from 'react';
import { AppBar, Toolbar, Typography, IconButton, Menu, MenuItem, Box, Button } from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import { useNavigate } from 'react-router-dom';
import './Navbar.css';

const pages = [
  { title: 'Home', route: '/home' },
  { title: 'Patient Records', route: '/patients' },
  { title: 'Appointments', route: '/appointments' },
  { title: 'AI Dental Assistant', route: '/ai-assistant' },
  { title: 'Illustrations', route: '/illustrations' },
  
];

const morePages = [

  { title: 'Inventory', route: '/stock' },
  { title: 'Finance & Billing', route: '/finance' },
  { title: 'Dental Diary', route: '/notes' },
  { title: 'Settings', route: '/config' },
];

const Navbar = () => {
  const navigate = useNavigate();

  const [anchorElNav, setAnchorElNav] = React.useState(null);
  const [anchorElMore, setAnchorElMore] = React.useState(null);

  const handleOpenNavMenu = (event) => {
    setAnchorElNav(event.currentTarget);
  };
  const handleCloseNavMenu = () => {
    setAnchorElNav(null);
  };

  const handleOpenMoreMenu = (event) => {
    setAnchorElMore(event.currentTarget);
  };
  const handleCloseMoreMenu = () => {
    setAnchorElMore(null);
  };

  return (
    <AppBar position="static" sx={{ backgroundColor: '#0077b6' }}>
      <Toolbar>
        {/* Logo or Title */}
        <Typography variant="h6" sx={{ flexGrow: 1, fontWeight: 600, letterSpacing: 1 }}>
          DentalBuddy
        </Typography>

        {/* Desktop Menu */}
        <Box sx={{ display: { xs: 'none', md: 'flex' }, gap: 2 }}>
          {pages.map((page) => (
            <Button key={page.title} sx={{ color: 'white' }} onClick={() => navigate(page.route)}>
              {page.title}
            </Button>
          ))}
          <IconButton onClick={handleOpenMoreMenu} sx={{ color: 'white' }}>
            <MoreVertIcon />
          </IconButton>
          <Menu anchorEl={anchorElMore} open={Boolean(anchorElMore)} onClose={handleCloseMoreMenu}>
            {morePages.map((page) => (
              <MenuItem key={page.title} onClick={() => { navigate(page.route); handleCloseMoreMenu(); }}>
                {page.title}
              </MenuItem>
            ))}
          </Menu>
        </Box>

        {/* Mobile Hamburger Menu */}
        <Box sx={{ display: { xs: 'flex', md: 'none' } }}>
          <IconButton onClick={handleOpenNavMenu} sx={{ color: 'white' }}>
            <MenuIcon />
          </IconButton>
          <Menu anchorEl={anchorElNav} open={Boolean(anchorElNav)} onClose={handleCloseNavMenu}>
            {[...pages, ...morePages].map((page) => (
              <MenuItem key={page.title} onClick={() => { navigate(page.route); handleCloseNavMenu(); }}>
                {page.title}
              </MenuItem>
            ))}
          </Menu>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
