import React from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  Menu,
  MenuItem,
  Box
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import { useNavigate } from 'react-router-dom';
import NavbarSearchModal from './NavbarSearchModal';

const pages = [
  { title: 'Home', route: '/home' },
  { title: 'Patient Records', route: '/patients' },
  { title: 'Appointments', route: '/appointments' },
  { title: 'AI Dental Assistant', route: '/ai-assistant' },
  { title: 'Illustrations', route: '/illustrations' },
];

const morePages = [
  { title: 'Inventory', route: '/stock' },
  { title: 'Finance & Billing', route: '/finance' },
  { title: 'Dental Diary', route: '/notes' },
  { title: 'Settings', route: '/config' },
];

const Navbar = () => {
  const navigate = useNavigate();
  const [anchorElNav, setAnchorElNav] = React.useState(null);
  const [anchorElMore, setAnchorElMore] = React.useState(null);

  const handleOpenNavMenu = (event) => setAnchorElNav(event.currentTarget);
  const handleCloseNavMenu = () => setAnchorElNav(null);

  const handleOpenMoreMenu = (event) => setAnchorElMore(event.currentTarget);
  const handleCloseMoreMenu = () => setAnchorElMore(null);

  return (
    <AppBar position="sticky" sx={{ backgroundColor: '#0077b6', boxShadow: 3 }}>
      <Toolbar sx={{ display: 'flex', justifyContent: 'space-between', flexWrap: 'wrap' }}>
        
        {/* Logo / Title */}
        <Typography
          variant="h6"
          noWrap
          sx={{
            fontWeight: 700,
            letterSpacing: 1,
            color: 'white',
            fontSize: { xs: '1.2rem', sm: '1.5rem' },
            flexGrow: 1,
            cursor: 'pointer'
          }}
          onClick={() => navigate('/home')}
        >
          import logo from '../assets/dental-buddy-logo.png'; 
          DentalBuddy
        </Typography>

        {/* Desktop Nav Links */}
        <Box sx={{ display: { xs: 'none', md: 'flex' }, gap: 3, alignItems: 'center' }}>
          {pages.map((page) => (
            <Typography
              key={page.title}
              onClick={() => navigate(page.route)}
              sx={{
                color: 'white',
                fontSize: '1rem',
                fontWeight: 500,
                cursor: 'pointer',
                '&:hover': {
                  textDecoration: 'underline',
                  color: '#e0f7fa',
                },
              }}
            >
              {page.title}
            </Typography>
          ))}

          {/* More dropdown icon */}
          <IconButton onClick={handleOpenMoreMenu} sx={{ color: 'white' }}>
            <MoreVertIcon />
          </IconButton>
          <Menu
            anchorEl={anchorElMore}
            open={Boolean(anchorElMore)}
            onClose={handleCloseMoreMenu}
          >
            {morePages.map((page) => (
              <MenuItem
                key={page.title}
                onClick={() => {
                  navigate(page.route);
                  handleCloseMoreMenu();
                }}
              >
                {page.title}
              </MenuItem>
            ))}
          </Menu>

          {/* Admin Search Modal */}
          <NavbarSearchModal />
        </Box>

        {/* Mobile Hamburger Menu */}
        <Box sx={{ display: { xs: 'flex', md: 'none' } }}>
          <IconButton onClick={handleOpenNavMenu} sx={{ color: 'white' }}>
            <MenuIcon />
          </IconButton>
          <Menu anchorEl={anchorElNav} open={Boolean(anchorElNav)} onClose={handleCloseNavMenu}>
            {[...pages, ...morePages].map((page) => (
              <MenuItem
                key={page.title}
                onClick={() => {
                  navigate(page.route);
                  handleCloseNavMenu();
                }}
              >
                {page.title}
              </MenuItem>
            ))}
          </Menu>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
