import React, { useState, useRef, useEffect } from 'react';
import './PatientForm.css';

const PatientForm = () => {
  const [formData, setFormData] = useState({
    name: '',
    age: '',
    gender: '',
    phone: '',
    address: '',
    date: '',
    referredBy: '',
    maritalStatus: '',
    chiefComplaint: [],
    medicalHistory: [],
    allergies: '',
    medications: '',
    dentalHistory: [],
    ccOther: '',
    dhOther: '',
    selectedQuadrant: '',
    quadrantTables: {
      first: false,
      second: false,
      third: false,
      fourth: false,
    },
    clinicalFindings: {
      first: Array(8).fill(null).map(() => ({ cavity: false, mobility: false, missing: false, filling: false })),
      second: Array(8).fill(null).map(() => ({ cavity: false, mobility: false, missing: false, filling: false })),
      third: Array(8).fill(null).map(() => ({ cavity: false, mobility: false, missing: false, filling: false })),
      fourth: Array(8).fill(null).map(() => ({ cavity: false, mobility: false, missing: false, filling: false })),
    },
    investigations: [],
    investigationOther: '',
    investigationFiles: [],
    diagnosis: '',
    treatmentPlan: []
  });

  const fileInputRef = useRef(null);
  const tableRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (
        tableRef.current &&
        !tableRef.current.contains(event.target) &&
        !event.target.closest('select[name="selectedQuadrant"]')
      ) {
        setFormData((prev) => ({ ...prev, quadrantTables: { ...prev.quadrantTables, [prev.selectedQuadrant]: false } }));
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({ ...prev, [name]: value }));
  };

  const handleMultiSelectChange = (e, key) => {
    const options = Array.from(e.target.selectedOptions, (option) => option.value);
    setFormData((prev) => ({ ...prev, [key]: options }));
  };

  const handleToothCheckboxChange = (quadrant, index, field) => {
    const updatedTeeth = [...formData.clinicalFindings[quadrant]];
    updatedTeeth[index] = {
      ...updatedTeeth[index],
      [field]: !updatedTeeth[index][field],
    };
    setFormData((prev) => ({
      ...prev,
      clinicalFindings: {
        ...prev.clinicalFindings,
        [quadrant]: updatedTeeth,
      },
    }));
  };

  const handleFileChange = (e) => {
    const files = Array.from(e.target.files);
    setFormData((prev) => ({ ...prev, investigationFiles: files }));
  };

  const handleQuadrantSelect = (e) => {
    const quadrant = e.target.value;
    setFormData((prev) => ({
      ...prev,
      selectedQuadrant: quadrant,
      quadrantTables: { ...prev.quadrantTables, [quadrant]: true },
    }));
  };

  const renderQuadrantTable = (quadrant) => (
    <div ref={tableRef} className="quadrant-table">
      <table className="tooth-table">
        <thead>
          <tr>
            <th>Tooth No.</th>
            <th>Cavity</th>
            <th>Mobility</th>
            <th>Missing</th>
            <th>Filling</th>
          </tr>
        </thead>
        <tbody>
          {formData.clinicalFindings[quadrant].map((tooth, index) => (
            <tr key={index}>
              <td>{index + 1}</td>
              {['cavity', 'mobility', 'missing', 'filling'].map((field) => (
                <td key={field}>
                  <input
                    type="checkbox"
                    checked={tooth[field]}
                    onChange={() => handleToothCheckboxChange(quadrant, index, field)}
                  />
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(formData);
    alert('Patient data saved successfully!');
  };

  return (
    <div className="form-container">
      <h2>Dental OP Case Sheet</h2>
      <form onSubmit={handleSubmit}>
        <fieldset>
          <legend>Patient Information</legend>
          <label>Name: <input type="text" name="name" value={formData.name} onChange={handleChange} /></label>
          <label>Age: <input type="number" name="age" value={formData.age} onChange={handleChange} /></label>
          <label>Gender:
            <select name="gender" value={formData.gender} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Male">Male</option>
              <option value="Female">Female</option>
              <option value="Other">Other</option>
            </select>
          </label>
          <label>Phone: <input type="text" name="phone" value={formData.phone} onChange={handleChange} /></label>
          <label>Address: <input type="text" name="address" value={formData.address} onChange={handleChange} /></label>
          <label>Date: <input type="date" name="date" value={formData.date} onChange={handleChange} /></label>
          <label>Referred By:
            <select name="referredBy" value={formData.referredBy} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Self">Self</option>
              <option value="Doctor">Doctor</option>
              <option value="Family/Friend">Family/Friend</option>
              <option value="Online">Online</option>
            </select>
          </label>
          <label>Marital Status:
            <select name="maritalStatus" value={formData.maritalStatus} onChange={handleChange}>
              <option value="">--Select--</option>
              <option value="Single">Single</option>
              <option value="Married">Married</option>
            </select>
          </label>
        </fieldset>

        <fieldset>
          <legend>Clinical Findings (Tooth-wise)</legend>
          <label>Quadrant:
            <select name="selectedQuadrant" value={formData.selectedQuadrant} onChange={handleQuadrantSelect}>
              <option value="">--Select Quadrant--</option>
              <option value="first">First Quadrant</option>
              <option value="second">Second Quadrant</option>
              <option value="third">Third Quadrant</option>
              <option value="fourth">Fourth Quadrant</option>
            </select>
          </label>
          {formData.selectedQuadrant && formData.quadrantTables[formData.selectedQuadrant] &&
            renderQuadrantTable(formData.selectedQuadrant)}
        </fieldset>

        <fieldset>
          <legend>Investigations</legend>
          <label>
            --Select--
            <select multiple value={formData.investigations} onChange={(e) => handleMultiSelectChange(e, 'investigations')}>
              {['IOPA X-Ray', 'OPG', 'CBCT', 'Pulp Vitality Test', 'Others'].map(inv => (
                <option key={inv} value={inv}>{inv}</option>
              ))}
            </select>
          </label>
          {formData.investigations.includes('Others') && (
            <label>
              Others: <textarea name="investigationOther" value={formData.investigationOther} onChange={handleChange} />
            </label>
          )}
          <label>
            Upload Documents:
            <input type="file" multiple onChange={handleFileChange} ref={fileInputRef} />
          </label>
        </fieldset>

        <fieldset>
          <legend>Diagnosis</legend>
          <label>
            Diagnosis:
            <textarea name="diagnosis" value={formData.diagnosis} onChange={handleChange} placeholder="Write diagnosis here..." rows="4" cols="50" />
          </label>
        </fieldset>

        <fieldset>
          <legend>Treatment Plan</legend>
          <label>
            --Select--
            <select multiple value={formData.treatmentPlan} onChange={(e) => handleMultiSelectChange(e, 'treatmentPlan')}>
              {['Filling', 'RCT', 'Extraction', 'Crown', 'Scaling', 'Braces', 'Implant'].map(tp => (
                <option key={tp} value={tp}>{tp}</option>
              ))}
            </select>
          </label>
        </fieldset>

        <button type="submit">Save Patient</button>
      </form>
    </div>
  );
};

export default PatientForm;
