import React, { useState } from 'react';

import {
  Modal,
  Box,
  TextField,
  List,
  ListItemButton,
  ListItemText,
  Typography,
  Fade,
  Backdrop,
  InputAdornment,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import SearchIcon from '@mui/icons-material/Search';

const searchItems = [
  { title: 'Patient Records', route: '/patients' },
  { title: 'Appointments', route: '/appointments' },
  { title: 'Finance & Billing', route: '/finance' },
  { title: 'Inventory Management', route: '/stock' },
  { title: 'Dental Diary', route: '/notes' },
  { title: 'AI Dental Assistant', route: '/ai-assistant' },
  { title: 'Illustrations', route: '/illustrations' },
  { title: 'Settings', route: '/config' },
];

const NavbarSearchModal = ({ open, onClose }) => {
  const [query, setQuery] = useState('');
  const navigate = useNavigate();

  const filtered = searchItems.filter((item) =>
    item.title.toLowerCase().includes(query.toLowerCase())
  );

  return (
    <Modal
      open={open}
      onClose={onClose}
      closeAfterTransition
      BackdropComponent={Backdrop}
      BackdropProps={{ timeout: 300 }}
    >
      <Fade in={open}>
        <Box
          sx={{
            position: 'absolute',
            top: '15%',
            left: '50%',
            transform: 'translateX(-50%)',
            width: '90%',
            maxWidth: 520,
            bgcolor: '#f0faff',
            borderRadius: 4,
            boxShadow: 10,
            p: 4,
            outline: 'none',
            border: '1px solid #0077b6',
          }}
        >
          <Typography
            variant="h6"
            sx={{
              mb: 2,
              color: '#0077b6',
              fontWeight: 600,
              textAlign: 'center',
              letterSpacing: 1,
            }}
          >
            üîç Search DentalBuddy
          </Typography>

          <TextField
            fullWidth
            autoFocus
            variant="outlined"
            placeholder="Search pages like Patients, Billing, Lab..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
            sx={{
              backgroundColor: 'white',
              borderRadius: 2,
              mb: 2,
            }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon sx={{ color: '#0077b6' }} />
                </InputAdornment>
              ),
            }}
          />

          <List sx={{ maxHeight: 300, overflowY: 'auto' }}>
            {filtered.length > 0 ? (
              filtered.map((item) => (
                <ListItemButton
                  key={item.title}
                  onClick={() => {
                    navigate(item.route);
                    onClose();
                  }}
                  sx={{
                    mb: 0.5,
                    borderRadius: 2,
                    '&:hover': {
                      backgroundColor: '#b2ebf2',
                      color: '#003f5c',
                    },
                  }}
                >
                  <ListItemText primary={item.title} />
                </ListItemButton>
              ))
            ) : (
              <Typography sx={{ mt: 2, textAlign: 'center' }} color="text.secondary">
                No matching pages found.
              </Typography>
            )}
          </List>
        </Box>
      </Fade>
    </Modal>
  );
};

export default NavbarSearchModal;
