import React, { useState, useEffect, useRef } from 'react';

import {
  Modal,
  Box,
  TextField,
  Typography,
  Fade,
  Backdrop,
  InputAdornment,
  List,
  ListItemButton,
  ListItemText,
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import SearchIcon from '@mui/icons-material/Search';

const keywordsToRoutes = {
  patient: '/patients',
  'add patient': '/patients',
  appointment: '/appointments',
  finance: '/finance',
  billing: '/finance',
  inventory: '/stock',
  lab: '/lab-tracking',
  illustration: '/illustrations',
  diary: '/notes',
  settings: '/config',
  };
const inputRef = useRef(null);

const staticAnswers = [
  {
    keyword: 'change password',
    answer: 'You can change admin settings under Settings > Security.',
  },
  {
    keyword: 'how to view finance',
    answer: 'Go to Finance & Billing from the main menu to view records.',
  },
  {
    keyword: 'where is lab tracking',
    answer: 'Lab Work Tracking is available under More > Lab Work Tracking.',
  },
];

const NavbarSearchModal = ({ open, onClose }) => {
  const [query, setQuery] = useState('');
  const navigate = useNavigate();

  const lowerQuery = query.toLowerCase().trim();

  //  1. Redirect on keyword match
  const matchedRoute = Object.keys(keywordsToRoutes).find((k) =>
    lowerQuery.includes(k)
  );

  //  2. Show answer if no route but matches FAQ
  const matchedAnswer = staticAnswers.find((item) =>
    lowerQuery.includes(item.keyword)
  );

  const handleSearch = () => {
    if (matchedRoute) {
      navigate(keywordsToRoutes[matchedRoute]);
      onClose();
    }
  };

  return (
    <Modal
      open={open}
      onClose={onClose}
      closeAfterTransition
      BackdropComponent={Backdrop}
      BackdropProps={{ timeout: 300 }}
    >
      <Fade in={open}>
        <Box
  sx={{
    position: 'absolute',
    top: '15%',
    left: '50%',
    transform: 'translateX(-50%)',
    width: '90%',
    maxWidth: 520,
    bgcolor: '#f0faff',
    borderRadius: 4,
    boxShadow: 10,
    p: 4,
    outline: 'none',
    border: '1px solid #0077b6',
  }}
>
          <Typography
            variant="h6"
            sx={{
              mb: 2,
              color: '#0077b6',
              fontWeight: 600,
              textAlign: 'center',
              letterSpacing: 1,
            }}
          >
            🔍 Search DentalBuddy
          </Typography>
         <TextField
           fullWidth
           autoFocus
           variant="outlined"
           placeholder="Search anything: patients, settings, billing..."
           value={query}
           onChange={(e) => setQuery(e.target.value)}
           onKeyDown={(e) => {
        if (e.key === 'Enter') handleSearch();
  }}
  InputProps={{
    startAdornment: (
      <InputAdornment position="start">
        <SearchIcon sx={{ color: '#0077b6' }} />
      </InputAdornment>
    ),
    style: {
      backgroundColor: '#ffffff',
      borderRadius: '8px',
      paddingLeft: '10px',
    },
  }}
  sx={{
    mb: 2,
    '& .MuiOutlinedInput-root': {
      '& fieldset': {
        borderColor: '#0077b6',
      },
      '&:hover fieldset': {
        borderColor: '#00b4d8',
      },
      '&.Mui-focused fieldset': {
        borderColor: '#0077b6',
      },
    },
  }}
/>


          {matchedRoute && (
            <List>
              <ListItemButton onClick={handleSearch}>
                <ListItemText
                  primary={`Go to ${keywordsToRoutes[matchedRoute].replace('/', '')}`}
                />
              </ListItemButton>
            </List>
          )}

          {matchedAnswer && (
            <Typography sx={{ mt: 2, color: '#333', textAlign: 'center' }}>
              💡 {matchedAnswer.answer}
            </Typography>
          )}

          {!matchedRoute && !matchedAnswer && query && (
            <Typography
              sx={{
                mt: 2,
                textAlign: 'center',
                fontStyle: 'italic',
                color: 'gray',
              }}
            >
              No exact match. Try keywords like “appointments” or “settings”.
            </Typography>
          )}
        </Box>
      </Fade>
    </Modal>
  );
};

export default NavbarSearchModal;
