import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useUserRole } from '../context/UserRoleContext';
import logo from '../assets/clinic-logo.png';

const Home = () => {
  const navigate = useNavigate();
  const { role } = useUserRole();

  const accessConfig = JSON.parse(localStorage.getItem('pageAccess')) || {};
  const userAccess = role === 'admin' ? null : accessConfig[role] || {};

  const quickLinks = [
    { title: 'Patient Records', route: '/patient-records', key: 'patientRecords' },
    { title: 'Appointments', route: '/appointments', key: 'appointments' },
    { title: 'AI Dental Assistant', route: '/ai-assistant', key: 'aiAssistant' },
    { title: 'Finance & Billing', route: '/finance', key: 'finance' },
    { title: 'Inventory Management', route: '/inventory', key: 'inventory' },
    { title: 'Lab Work Tracking', route: '/lab-tracking', key: 'lab' },
    { title: 'Illustrations', route: '/illustrations', key: 'illustrations' },
    { title: 'Dental Diary', route: '/notes', key: 'notes' },
  ];

  const visibleLinks = role === 'admin'
    ? quickLinks
    : quickLinks.filter(link => userAccess[link.key]);

  return (
    <div className="home-container">
      <div className="clinic-header">
        <div className="logo-title">
          <img src={logo} alt="Clinic Logo" className="home-logo" />
          <h1>Elite Dental Clinic</h1>
        </div>
        <p className="doctor-names">
          Dr. Chikku Paulo, B.D.S &nbsp;&nbsp;&nbsp; Dr. Gayathri Balagopal, B.D.S
        </p>
      </div>

      <div className="quick-links">
        {visibleLinks.map((link, idx) => (
          <div key={idx} className="card" onClick={() => navigate(link.route)}>
            <h3>{link.title}</h3>
            <button>Go</button>
          </div>
        ))}
      </div>
    </div>
  );
};
export default Home;
