import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useUserRole } from '../context/UserRoleContext';
import './Home.css';


const Home = () => {
  const navigate = useNavigate();
  const { role } = useUserRole();
  const safeRole = role || 'assistant';

  const access = JSON.parse(localStorage.getItem('pageAccess')) || {
    admin: {},
    assistant: {},
    lab: {},
  };

  const allLinks = [
    { title: 'Patient Records', route: '/patients', key: 'patientRecords' },
    { title: 'Appointments', route: '/appointments', key: 'appointments' },
    { title: 'AI Dental Assistant', route: '/ai-assistant', key: 'aiAssistant' },
    { title: 'Finance & Billing', route: '/finance', key: 'finance' },
    { title: 'Inventory Management', route: '/stock', key: 'inventory' },
    { title: 'Lab Work Tracking', route: '/lab-tracking', key: 'lab' },
    { title: 'Illustrations', route: '/illustrations', key: 'illustrations' },
    { title: 'Dental Diary', route: '/notes', key: 'notes' },
    { title: 'Access Settings', route: '/config', key: 'config' }, // admin only
  ];

  return (
    <div className="home-container">
      {/* Header (clinic name + doctors in flex box) */}
      <div className="clinic-header">
        <div className="clinic-title-box">
          <h1 className="clinic-name">ELITE DENTAL CLINIC</h1>
          <p className="doctor-names">
            Dr. Chikku Paulo, B.D.S &nbsp;&nbsp;&nbsp; Dr. Gayathri Balagopal, B.D.S
          </p>
        </div>
      </div>

      {/* Cards section */}
      <div className="quick-links">
        {allLinks.map((link, idx) => {c
          const isAllowed = safeRole === 'admin' || access[safeRole]?.[link.key];
          return (
            <div
              key={idx}
              className={`card ${!isAllowed ? 'locked' : ''}`}
              onClick={() => isAllowed && navigate(link.route)}
              title={!isAllowed ? 'Access Denied - Not Assigned' : ''}
              style={{ cursor: isAllowed ? 'pointer' : 'not-allowed' }}
            >
              <h3>{link.title}</h3>
              <button disabled={!isAllowed}>
                {isAllowed ? 'Go' : 'ðŸ”’'}
              </button>
            </div>
          );
        })}
      </div>
    </div>
  );
};

export default Home;
