import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { IconButton } from '@mui/material';
import SaveIcon from '@mui/icons-material/Save';
import DeleteSweepIcon from '@mui/icons-material/DeleteSweep';
import AssignmentIcon from '@mui/icons-material/Assignment';

const ImportantNotePR = () => {
  const [note, setNote] = useState('');
  const [noteId, setNoteId] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchNote = async () => {
      try {
        const res = await axios.get('/api/notes/patient-records');
        if (res.data && res.data.content) {
          setNote(res.data.content);
          setNoteId(res.data._id);
        }
      } catch (error) {
        console.error('Error fetching note:', error.message);
      }
    };

    fetchNote();
  }, []);

  const handleSave = async () => {
    const newNoteData = {
      page: 'patient-records',
      content: note,
      createdBy: 'Admin',
      isSaved: false
    };

    try {
      const res = await axios.post('/api/notes', newNoteData);
      setNoteId(res.data._id);
      setMessage('Note saved!');
    } catch (error) {
      console.error('Error saving note:', error.response?.data?.message || error.message);
      setMessage('âŒ Failed to save note.');
    }
  };

  const handleClear = () => {
    setNote('');
    setMessage('ðŸ§¹ leared');
  };

  return (
    <div
      style={{
        padding: '15px',
        borderRadius: '15px',
        backgroundColor: 'white',
        boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
        maxWidth: '100%',
        width: '100%',
        minWidth: '250px',
        boxSizing: 'border-box',
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      {/* Header with title and buttons */}
      <div
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'space-between',
          alignItems: 'center',
          marginBottom: '10px',
          gap: '10px'
        }}
      >
        <div style={{ display: 'flex', alignItems: 'center', gap: '8px' }}>
          <AssignmentIcon style={{ fontSize: '28px' }} />
          <h3 style={{ margin: 0, fontSize: '1.2rem' }}>Important Notes</h3>
        </div>
        <div style={{ display: 'flex', gap: '6px' }}>
          <IconButton onClick={handleSave} title="Save" style={{ backgroundColor: '#007b8a', color: 'white' }}>
            <SaveIcon />
          </IconButton>
          <IconButton onClick={handleClear} title="Clear Note" style={{ backgroundColor: '#f57c00', color: 'white' }}>
            <DeleteSweepIcon />
          </IconButton>
        </div>
      </div>

      {/* Editable Textarea */}
      <textarea
        rows={4}
        style={{
          width: '100%',
          padding: '12px',
          fontSize: '1rem',
          border: '2px solid #333',
          borderRadius: '10px',
          backgroundColor: '#f7f7f7',
          color: '#333',
          resize: 'none',
          overflowY: 'auto',
          height: '80px',
          fontFamily: 'inherit',
          boxSizing: 'border-box',
          marginBottom: '10px' 
        }}
        placeholder="Type here...."
        value={note}
        onChange={(e) => setNote(e.target.value)}
      ></textarea>

      {/* Feedback Message */}
      {message && (
        <div
          style={{
            fontSize: '0.9rem',
            color: message.includes('âœ…') ? 'green' : message.includes('âŒ') ? 'red' : '#555',
            backgroundColor: '#f0f0f0',
            padding: '6px 10px',
            borderRadius: '6px',
            minHeight: '22px'
          }}
        >
          {message}
        </div>
      )}
    </div>
  );
};

export default ImportantNotePR;
