import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './PatientList.css';

const PatientList = () => {
  const [patients, setPatients] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPatients = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/patients');
        setPatients(response.data);
      } catch (error) {
        console.error('Error fetching patients:', error);
      }
    };

    fetchPatients();
  }, []);

  const handleViewDetails = (id) => {
    navigate(`/patients/${id}`);
  };

  const formatDate = (dateStr) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString(); // formats to "dd/mm/yyyy" or "mm/dd/yyyy" depending on locale
  };

  return (
    <div className="patient-list-container">
      <h2>PATIENT LIST</h2>
      <table className="patient-table">
        <thead>
          <tr>
            <th>Serial No.</th>
            <th>Date</th>
            <th>Name</th>
            <th>Age</th>
            <th>Gender</th>
            <th>Chief Complaint</th>
            <th>Diagnosis</th>
            <th>Treatment Plan</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {patients.map((patient, index) => (
            <tr key={patient._id}>
              <td>{index + 1}</td>
              <td>{formatDate(patient.date)}</td>
              <td>{patient.name}</td>
              <td>{patient.age}</td>
              <td>{patient.gender}</td>
              <td>{patient.chiefComplaint?.join(', ')}</td>
              <td>{patient.diagnosis}</td>
              <td>{patient.treatmentPlan?.join(', ')}</td>
              <td>
                <button className="details-btn" onClick={() => handleViewDetails(patient._id)}>Details</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PatientList;
