import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useUserRole } from '../context/UserRoleContext';

const LoginPage = () => {
  const [selectedRole, setSelectedRole] = useState('');
  const [password, setPassword] = useState('');
  const { setRole } = useUserRole();
  const navigate = useNavigate();

  const handleLogin = () => {
    if (selectedRole === 'admin') {
      if (password === 'admin123') {
        setRole('admin');
        navigate('/home');
      } else {
        alert('Incorrect admin password');
      }
    } else if (selectedRole !== '') {
      setRole(selectedRole);
      navigate('/home');
    } else {
      alert('Please select a role');
    }
  };

  return (
    <div className="login-container">
      <h2>Select Your Role</h2>
      <select value={selectedRole} onChange={(e) => setSelectedRole(e.target.value)}>
        <option value="">-- Choose Role --</option>
        <option value="admin">1. Admin / Doctor</option>
        <option value="nurse">2. Dental Nurse</option>
        <option value="front">3. Front Office Staff</option>
        <option value="accountant">4. Accountant</option>
        <option value="lab">5. Lab Staff</option>
        <option value="manager">6. Manager</option>
      </select>

      {selectedRole === 'admin' && (
        <input
          type="password"
          placeholder="Enter Admin Password"
          onChange={(e) => setPassword(e.target.value)}
        />
      )}

      <button onClick={handleLogin}>Continue</button>
    </div>
  );
};

export default LoginPage;
