import React, { useState, useEffect } from 'react';
import axios from 'axios';

const ImportantNotePR = () => {
  const [note, setNote] = useState('');
  const [noteId, setNoteId] = useState(null);
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get('/api/important-note-pr')
      .then((res) => {
        if (res.data) {
          setNote(res.data.message);
          setNoteId(res.data._id);
        }
      });
  }, []);

  const handleSave = () => {
    axios.post('/api/important-note-pr', { message: note, author: 'User' })
      .then((res) => {
        setMessage('Note saved!');
        setNoteId(res.data._id);
      });
  };

  const handleDelete = () => {
    axios.delete(`/api/important-note-pr/${noteId}`)
      .then(() => {
        setNote('');
        setNoteId(null);
        setMessage('Note deleted!');
      });
  };

  return (
    <div style={{
      // background: '#fff8e1',
      padding: '20px',
      borderRadius: '10px',
      marginTop: '20px',
      boxShadow: '0 2px 6px rgba(0,0,0,0.1)',
      maxWidth: '600px',
      marginLeft: 'auto',
      marginRight: 'auto'
    }}>
      <h3>üìù Important Notes</h3>
      <textarea
        rows={5}
        style={{ width: '100%', padding: '10px', fontSize: '1rem' }}
        placeholder="Type your important note here..."
        value={note}
        onChange={(e) => setNote(e.target.value)}
      ></textarea>
      <div style={{ marginTop: '10px' }}>
        <button onClick={handleSave}>üíæ Save</button>
        {noteId && <button onClick={handleDelete} style={{ marginLeft: '10px' }}>üóë Delete</button>}
      </div>
      {message && <p style={{ color: 'green' }}>{message}</p>}
    </div>
  );
};

export default ImportantNotePR;
