import React from 'react';
import {
  AppBar,
  Toolbar,
  Typography,
  IconButton,
  Menu,
  MenuItem,
  Box,
  Button,
  Tooltip,
} from '@mui/material';
import MenuIcon from '@mui/icons-material/Menu';
import MoreVertIcon from '@mui/icons-material/MoreVert';
import SearchIcon from '@mui/icons-material/Search';
import { useNavigate } from 'react-router-dom';
import { useUserRole } from '../context/UserRoleContext';
import logo from './assets/dentalbuddy-logo.png';


// Icons
import MedicalServicesIcon from '@mui/icons-material/MedicalServices';
import EventAvailableIcon from '@mui/icons-material/EventAvailable';
import SmartToyIcon from '@mui/icons-material/SmartToy';
import ImageIcon from '@mui/icons-material/Image';
import Inventory2Icon from '@mui/icons-material/Inventory2';
import AttachMoneyIcon from '@mui/icons-material/AttachMoney';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import SettingsIcon from '@mui/icons-material/Settings';

const iconMap = {
  Home: <MedicalServicesIcon sx={{ mr: 1 }} />,
  'Patient Records': <MedicalServicesIcon sx={{ mr: 1 }} />,
  Appointments: <EventAvailableIcon sx={{ mr: 1 }} />,
  'AI Dental Assistant': <SmartToyIcon sx={{ mr: 1 }} />,
  Illustrations: <ImageIcon sx={{ mr: 1 }} />,
  Inventory: <Inventory2Icon sx={{ mr: 1, color: '#0077b6' }} />,
  'Finance & Billing': <AttachMoneyIcon sx={{ mr: 1, color: '#0077b6' }} />,
  'Dental Diary': <MenuBookIcon sx={{ mr: 1, color: '#0077b6' }} />,
  Settings: <SettingsIcon sx={{ mr: 1, color: '#0077b6' }} />,
};

const pages = [
  { title: 'Home', route: '/home' },
  { title: 'Patient Records', route: '/patients' },
  { title: 'Appointments', route: '/appointments' },
  { title: 'AI Dental Assistant', route: '/ai-assistant' },
  { title: 'Illustrations', route: '/illustrations' },
];

const morePages = [
  { title: 'Inventory', route: '/stock' },
  { title: 'Finance & Billing', route: '/finance' },
  { title: 'Dental Diary', route: '/notes' },
  { title: 'Settings', route: '/config' },
];

const Navbar = () => {
  const navigate = useNavigate();
  const { role } = useUserRole();
  const [anchorElNav, setAnchorElNav] = React.useState(null);
  const [anchorElMore, setAnchorElMore] = React.useState(null);
  const [searchOpen, setSearchOpen] = React.useState(false);

  const handleOpenNavMenu = (e) => setAnchorElNav(e.currentTarget);
  const handleCloseNavMenu = () => setAnchorElNav(null);
  const handleOpenMoreMenu = (e) => setAnchorElMore(e.currentTarget);
  const handleCloseMoreMenu = () => setAnchorElMore(null);
  const handleOpenSearch = () => setSearchOpen(true); 

  return (
    <AppBar
      position="sticky"
      sx={{
        background: 'linear-gradient(to right, #0077b6, #00b4d8)',
        boxShadow: 3,
      }}
    >
      <Toolbar
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          flexWrap: 'wrap',
        }}
      >
        <Typography
          variant="h6"
          noWrap
          component="div"
          sx={{
            fontWeight: 700,
            letterSpacing: 1,
            color: 'white',
            fontSize: { xs: '1.2rem', sm: '1.5rem' },
            flexGrow: 1,
          }}
        >
         <Box
  sx={{
    display: 'flex',
    alignItems: 'center',
    gap: 1,
    cursor: 'pointer',
    mr: 2,
  }}
  onClick={() => navigate('/home')}
>
  <img
    src={logo}
    alt="DentalBuddy Logo"
    style={{ height: '40px', width: 'auto' }} // You can tweak size here
  />
  <Typography
    variant="h6"
    sx={{
      fontWeight: 700,
      color: 'white',
      fontSize: { xs: '1rem', sm: '1.2rem' },
    }}
  >

  </Typography>
</Box>

        </Typography>

        <Box sx={{ display: { xs: 'none', md: 'flex' }, alignItems: 'center', gap: 2 }}>
          {pages.map((page) => (
            <Button
              key={page.title}
              onClick={() => navigate(page.route)}
              disableRipple
              variant="text"
              sx={{
                backgroundColor: 'transparent !important',
                color: '#ffffff',
                fontWeight: 600,
                fontSize: '0.95rem',
                px: 1.5,
                textTransform: 'uppercase',
                borderBottom: '3px solid transparent',
                '&:hover': {
                  borderBottom: '3px solid #ffffff',
                  backgroundColor: 'transparent !important',
                },
              }}
            >
              {iconMap[page.title]} {page.title}
            </Button>
          ))}

          <IconButton onClick={handleOpenMoreMenu} sx={{ color: 'white' }}>
            <MoreVertIcon />
          </IconButton>

          <Menu
            anchorEl={anchorElMore}
            open={Boolean(anchorElMore)}
            onClose={handleCloseMoreMenu}
            PaperProps={{
              sx: {
                borderRadius: 2,
                background: 'linear-gradient(145deg, #e0f7fa, #ffffff)',
                boxShadow: 4,
                minWidth: 220,
                py: 1,
              },
            }}
          >
            {morePages.map((page) => (
              <MenuItem
                key={page.title}
                onClick={() => {
                  navigate(page.route);
                  handleCloseMoreMenu();
                }}
                sx={{
                  fontWeight: 600,
                  textTransform: 'uppercase',
                  color: '#0077b6',
                  '&:hover': {
                    backgroundColor: '#b2ebf2',
                    color: '#003f5c',
                  },
                }}
              >
                {iconMap[page.title]} {page.title}
              </MenuItem>
            ))}
          </Menu>

          {role === 'admin' && (
            <Tooltip title="Search">
              <IconButton
                onClick={handleOpenSearch}
                sx={{ color: 'white' }}
              >
                <SearchIcon />
              </IconButton>
            </Tooltip>
          )}
        </Box>

        {/* Mobile View */}
        <Box sx={{ display: { xs: 'flex', md: 'none' } }}>
          <IconButton onClick={handleOpenNavMenu} sx={{ color: 'white' }}>
            <MenuIcon />
          </IconButton>
          <Menu
            anchorEl={anchorElNav}
            open={Boolean(anchorElNav)}
            onClose={handleCloseNavMenu}
          >
            {[...pages, ...morePages].map((page) => (
              <MenuItem
                key={page.title}
                onClick={() => {
                  navigate(page.route);
                  handleCloseNavMenu();
                }}
              >
                {iconMap[page.title]} {page.title}
              </MenuItem>
            ))}
          </Menu>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
